mapper.select:
select count(userId)
from member
where userId = #{userid}

select userId, userName
from member
where userId = #{userid} and userPwd = #{userpwd}

select user_id, user_name, user_password, email_id, email_domain, join_date
from member
where user_id = #{userId}

mapper.update:
update member
user_password = #{userPwd}, email_id = #{emailId}, email_domain = #{emailDomain},
where user_id = #{userId}
