mapper.select:
SELECT * FROM mountain 
WHERE mntilistno NOT IN (
SELECT mm.mntilistno FROM member_mountains mm
WHERE mm.memberid = #{memberid}
)

select count(*) 
from membermountain mm
where mm.userId=#{memberid} and mm.mntilistno=#{mntilistno}

mapper.update:
update mountain set
mnticonquerednum=mnticonquerednum+1 where
mntilistno=#{mntilistno}
